	/**
	 * 主体部分
	 */
	for (let website of websites) {
		if (location.href.indexOf(website.url) != -1) {
			//隐藏/拦截骚扰元素
			if (website.items) {
				for (let item of website.items) {
					addStyle(item + "{display: none !important}");
				}
			}
			//修复移动版页面不允许滑动
			if (website.overflow) {
				addStyle("body{overflow: auto !important}");
			}
			//执行额外方案
			if (website.fun) {
				website.fun();
			}
		}
	}

	//默认监听
	let LoginFlag = true;
	//默认添加
	let ClickFlag = true;

	/**
	 * 通过内容(xpath)获取节点
	 *
	 * @param      {string}  xpath   内容
	 * @param      {Node}  parent  父元素
	 * @return     {Node}  元素
	 */
	function getXpath(xpath, parent) {
		let xpathResult = document.evaluate(xpath, parent || document, null, XPathResult.FIRST_ORDERED_NODE_TYPE, null);
		return xpathResult.singleNodeValue;
	}

	/**
	 * 向head标签内添加样式
	 *
	 * @param      {string}  text    CSS样式
	 */
	function addStyle(text) {
		let css = document.createElement("style");
		css.innerText += text;
		document.head.append(css);
	}

	/**
	 * 登录弹窗只弹出一次时的处理方案
	 *
	 * @param      {string}  unLoginBtn  The un login button class
	 * @param      {string}  closeBtn    The close button class
	 */
	function loginObserver(unLoginBtn, closeBtn) {
		let mo = new MutationObserver(function (mutations) {
			let unloginBtn = document.querySelector(unLoginBtn);
			if (unloginBtn) {
				for (let mutation of mutations) {
					for (let node of mutation.addedNodes) {
						let button = node.querySelector(closeBtn);
						if (button) {
							console.log("关闭成功");
							button.click();
							mo.disconnect();
						}
					}
				}
			}
		});
		mo.observe(document, { childList: true, subtree: true });
	}
